#include<bits/stdc++.h>using namespace std;int main(){    long long t;    cin>>t;    while(t--){        long long n;        cin>>n;        string str;        cin>>str;        vector<long long>sheeps;        for(long long i=0;i<n;i++){            if(str[i]=='*') sheeps.push_back(i);        }        long long mid=(sheeps.size())/2;        long long ans=0;        for(long long i=0;i<sheeps.size();i++){            if(i==mid) continue;            else if(i<mid){                ans+=(sheeps[mid]-sheeps[i]-1)-i;            }            else{                ans+=(sheeps[i]-sheeps[mid]-1)-(i-mid-1);            }        }        cout<<ans<<"\n";    }}