#include<bits/stdc++.h>using namespace std;int main(){    int t;    cin>>t;    while(t--){        int n,j,k;        cin>>n>>j>>k;        vector<int>arr(n);        for(int i=0;i<n;i++){            cin>>arr[i];        }        int cnt=0,maxi=INT_MIN;        for(auto i : arr){            maxi=max(i,maxi);            if(arr[j-1]==i) cnt++;        }        if(k>1) cout<<"Yes"<<"\n";        else if(maxi!=arr[j-1]) cout<<"No"<<"\n";        else cout<<"Yes"<<"\n";    }}