#include<bits/stdc++.h>using namespace std;int main(){    int t;    cin>>t;    while(t--){        long long n;        cin>>n;        vector<long long>odd;        long long ans=0,temp;        for(int i=0;i<n;i++){            cin>>temp;            if(temp&1){                odd.push_back(temp);            }            else ans+=temp;        }        sort(odd.begin(),odd.end(),greater<int>());        int oddSize=(odd.size()+1)/2;        for(int i=0;i<oddSize;i++) ans+=odd[i];        if(odd.size()==0) cout<<0<<"\n";        else cout<<ans<<"\n";    }}