#include<bits/stdc++.h>using namespace std;int main(){    long long t;    cin>>t;    while(t--){        long long n;        cin>>n;        unordered_map<long long,vector<long long>>Map;        vector<vector<long long>>arrays(n);        long long ansSize=0;        for(long long a=0;a<n;a++){            long long size;            cin>>size;            vector<long long>arr(size);            for(long long i=0;i<size;i++){                cin>>arr[i];                if(Map.find(i)==Map.end()) Map[i]={arr[i],size-1,a};                else if(arr[i]<Map[i][0]) Map[i]={arr[i],size-1,a};                else if(arr[i]==Map[i][0]) Map[i]={arr[i],i,a};            }            ansSize=max(ansSize,size);            arrays[a]=arr;        }        vector<long long>ans(ansSize);        long long st=0,ed;        while(true){            ed=Map[st][1];            int a=Map[st][2];            for(int i=st;i<=ed;i++){                ans[i]=arrays[a][i];            }            st=ed+1;            if(st>=ansSize) break;        }        for(auto i : ans) cout<<i<<" ";        cout<<endl;    }}