#include<bits/stdc++.h>using namespace std;int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        string s1,s2;        cin>>s1>>s2;        bool flag=true;        unordered_map<char,int>Map;        for(auto i : s1) Map[i]++;        for(auto i : s2){            if(Map.find(i)==Map.end()){                flag=false;                break;            }            else{                Map[i]--;                if(Map[i]==0){                    Map.erase(i);                }            }        }        if(flag){            cout<<"YES"<<"\n";        }        else{            cout<<"NO"<<"\n";        }    }}