#include<bits/stdc++.h>using namespace std;int main(){    long long t;    cin>>t;    while(t--){        long long n;        cin>>n;        vector<long long>arr1(n),arr2(n);        long long mini=LLONG_MAX, maxi=LLONG_MIN;        for(int i=0;i<n;i++){            cin>>arr1[i];            arr2[i]=arr1[i];            mini=min(mini,arr1[i]);            maxi=max(maxi,arr1[i]);        }        long long ans=LLONG_MAX;        sort(arr2.begin(),arr2.end());        for(int i=0;i<n;i++){            if(arr1[i]!=arr2[i]){                ans=min(ans,max(abs(mini-arr1[i]),abs(maxi-arr1[i])));            }        }Â         if(ans==LLONG_MAX) cout<<"-1"<<"\n";        else cout<<ans<<"\n";    }}