#include<bits/stdc++.h>using namespace std;int main(){    int t;    cin >> t;    while (t--) {        int n;        cin>>n;        vector<int>arr(n);        for(int i=0;i<n;i++){            cin>>arr[i];        }        int i=1,ans=0;        while(i<n){            if(arr[i-1]==(7-arr[i]) || arr[i-1]==arr[i]){                ans++;                i++;            }            i++;        }        cout<<ans<<"\n";    }}