#include <bits/stdc++.h>using namespace std; #define ll long long int main() {    ios::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin >> t;    while (t--) {        int n,x,y;        cin>>n>>x>>y;        vector<int>per(n);        for(int i=0;i<n;i++) cin>>per[i];        vector<int>inside,outside;        for(int i=0;i<n;i++){            if(i>=x and i<y) inside.push_back(per[i]);            else outside.push_back(per[i]);        }        int mini=INT_MAX,idx=0;        for(int i=0;i<inside.size();i++){            if(inside[i]<mini){                mini=inside[i];                idx=i;            }        }        deque<int>ans;        for(int i=idx;i<inside.size();i++) ans.push_back(inside[i]);        for(int i=0;i<idx;i++) ans.push_back(inside[i]);        int val=ans.front();        idx=0;        while(idx<outside.size() and outside[idx]<val) idx++;        idx--;        if(idx>=0){            for(int i=idx;i>=0;i--) ans.push_front(outside[i]);        }        for(int i=idx+1;i<outside.size();i++) ans.push_back(outside[i]);         for(auto i : ans) cout<<i<<" ";        cout<<"\n";    }     return 0;}