#include<bits/stdc++.h>using namespace std;Â int main() {    int n, l;    cin>>n>>l;    vector<int> v(n);    for(int i = 0; i < n; i++) cin>>v[i];    sort(v.begin(), v.end());    double ans = 0;    if(v[0] != 0) ans = v[0];    int prev = v[0];    for(int i = 1; i < n; i++) {        ans = max(ans, (v[i] - prev) / 2.0);        prev = v[i];    }	if(v[n - 1] != l) ans = max(ans, double(l - v[n - 1]));    printf("%.10f", ans);}